import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom";
import firebase from "./firebase";
import DataTable from 'react-data-table-component';
import { Button, Container, Label, Modal, ModalHeader, ModalBody, ModalFooter, CustomInput, Tooltip } from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faBars } from '@fortawesome/free-solid-svg-icons'
import FilterComponent from '../../components/InputBusquedaDT';
import LoadSpinner from '../../components/LoadSpinner';
import { paginationOptions, paginationPerPage, paginationRowsPerPageOptions, fixedHeaderScrollHeight }
    from '../../components/DataTableConfig';
//import Cookies from 'universal-cookie';
import ToastMessage from '../../components/ToastMessage';


const Product = () => {
    //const baseUrl = `${configData.SERVER_URL}Conciliacion/`;
    //const cookies = new Cookies();
    const history = useHistory();

    const mensajeVacio = { show: false, icon: "white", title: "", message: "", position: "10%"  }
    const [mensaje, setMensaje] = useState(mensajeVacio);

    const closeMensaje = () => {
        setTimeout(() => {
            setMensaje(mensajeVacio)
        }, 3000);
    };

    const encabezadoProducto = {
        Codigo_Producto: '',
        Fecha_Registro: new Date(),
        Cantidad_Existencia: 0,
        Nombre_Producto: '',
        Descripcion_Producto: ''
    };

    const [dataProducto, setDataProducto] = useState([encabezadoProducto]);
    const [dataShowProducto, setDataShowProducto] = useState([encabezadoProducto]);
    const [modalMostrar, setModalMostrar] = useState(false);
    const [modalInsertar, setModalInsertar] = useState(false);
    const [modalEliminar, setModalEliminar] = useState(false);
    const [loading, setLoading] = useState(false);

    const accionesOrdenes = (row) => {
        setDataShowProducto(row);
        abrirCerrarModalMostrar();
    }

    const columns = [
        {
            name: 'Ver',
            button: true,
            cell: (row) => <Button className="btn btn-success" onClick={() => accionesProducto(row, "ver")}><FontAwesomeIcon icon={faBars} /></Button>
        },
        {
            name: 'Número Orden',
            selector: 'Codigo_Producto',
            sortable: true,
            width: '110px',
            right: true
        },
        {
            name: 'Fecha Registro',
            selector: 'Fecha_Registro',
            sortable: true,
            left: true,
        },
        {
            name: 'Existencia',
            selector: 'Cantidad_Existencia',
            sortable: true,
            left: true,
        },
        {
            name: 'Nombre Producto',
            selector: 'Nombre_Producto',
            sortable: true,
            left: true,
            width: '300px'
        }
    ];

    const abrirCerrarModalMostrar = () => {
        setModalMostrar(!modalMostrar);
    };

    //Hooks & Functions for Input Filter
    const [filterText, setFilterText] = useState('');
    const [resetPaginationToggle, setResetPaginationToggle] = useState(false);
    const filteredItems = (filterText === '') ? dataProducto : dataProducto.filter(item => (item.Asiento_Id && item.Asiento_Id.toString().toLowerCase().includes(filterText.toLowerCase()))
        || (item.Codigo_Producto && item.Codigo_Producto.toString().toLowerCase().includes(filterText.toLowerCase()))
    );

    const subHeaderComponentMemo = React.useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setResetPaginationToggle(!resetPaginationToggle);
                setFilterText('');
            }
        };

        return <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} placeholder={"Cód. Producto"} />;
    }, [filterText, resetPaginationToggle]);

    const obtenerProductos = () => {
        firebase.child("producto").on("value", (producto) => {
            if (producto.val() !== null) {
                setDataProducto(producto.val());
            } else {
                setDataProducto(encabezadoProducto);
            }
        });
    };

    const guardarProducto = () => {
        firebase.child("canales").push(this.state.form,
            error => {
                if (error) console.log(error)
            });
        this.setState({ modalInsertar: false });
    };

    const actualizarProducto = () => {
        firebase.child(`canales/${this.state.id}`).set(
            this.state.form,
            error => {
                if (error) console.log(error)
            });
        this.setState({ modalEditar: false });
    };

    const eliminarProducto = () => {
        if (window.confirm(`Estás seguro que deseas eliminar el producto ${this.state.form && this.state.form.canal}?`)) {
            firebase.child(`canales/${this.state.id}`).remove(
                error => {
                    if (error) console.log(error)
                });
        }
    }

    useEffect(() => {
        obtenerProductos();
        /*if (cookies.get('id')) {
        }*/
    }, []);// eslint-disable-line react-hooks/exhaustive-deps


    return (
        <div>
            {loading ? <LoadSpinner Mensaje="Cargando, Por Favor Espere" /> : ""}
            <ToastMessage message={mensaje.message} title={mensaje.title} show={mensaje.show} icon={mensaje.icon} position={mensaje.position} />
            <Container>
                <div className="">
                    <h3 className="text-primary">Lista de Productos</h3>
                </div>
                <div className="table-responsive">
                    <DataTable
                        columns={columns}
                        data={filteredItems}
                        pagination
                        paginationResetDefaultPage={resetPaginationToggle}
                        subHeader
                        subHeaderComponent={subHeaderComponentMemo}
                        persistTableHeadpaginationComponentOptions={paginationOptions}
                        fixedHeader
                        fixedHeaderScrollHeight={fixedHeaderScrollHeight}
                        noHeader={true}
                        paginationPerPage={paginationPerPage}
                        paginationRowsPerPageOptions={paginationRowsPerPageOptions}
                        noDataComponent={paginationOptions.noDataComponent}
                    />
                </div>
                <Modal isOpen={modalMostrar}>
                    <ModalHeader>
                        Producto N° {dataShowProducto && dataShowProducto[0].Codigo_Producto}
                    </ModalHeader>
                    <ModalBody>
                        <div className="table-responsive detalle-cuenta">
                            <table className='table table-striped' aria-labelledby="tabelLabel">
                                <thead>
                                    <tr>
                                        <th>Código Producto</th>
                                        <th>Nombre Producto</th>
                                        <th>Descripción</th>
                                        <th>Existencia</th>
                                        <th>Registro</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>{dataShowProducto.Codigo_Producto}</td>
                                        <td>{dataShowProducto.Nombre_Producto}</td>
                                        <td>{dataShowProducto.Descripcion_Producto}</td>
                                        <td>{dataShowProducto.Cantidad_Existencia}</td>
                                        <td>{dataShowProducto.Fecha_Registro}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-secondary" onClick={() => abrirCerrarModalMostrar()}>Cerrar</button>
                    </ModalFooter>
                </Modal>
                <Modal isOpen={modalInsertar}>
                    <ModalHeader>Insertar Producto</ModalHeader>
                    <ModalBody>
                        <div className="form-group">
                            <label>Canal: </label>
                            <br />
                            <input type="text" className="form-control" name="canal" onChange={this.handleChange} />
                            <br />
                            <label>País: </label>
                            <br />
                            <input type="text" className="form-control" name="pais" onChange={this.handleChange} />
                            <br />
                            <label>Idioma: </label>
                            <br />
                            <input type="text" className="form-control" name="idioma" onChange={this.handleChange} />
                            <br />
                            <label>Cantidad de Suscriptores (millones): </label>
                            <br />
                            <input type="text" className="form-control" name="suscriptores" onChange={this.handleChange} />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-primary" onClick={() => this.peticionPost()}>Insertar</button>{"   "}
                        <button className="btn btn-danger" onClick={() => this.setState({ modalInsertar: false })}>Cancelar</button>
                    </ModalFooter>
                </Modal>



                <Modal isOpen={modalModificar}>
                    <ModalHeader>Editar Producto</ModalHeader>
                    <ModalBody>
                        <div className="form-group">
                            <label>Canal: </label>
                            <br />
                            <input type="text" className="form-control" name="canal" onChange={this.handleChange} value={this.state.form && this.state.form.canal} />
                            <br />
                            <label>País: </label>
                            <br />
                            <input type="text" className="form-control" name="pais" onChange={this.handleChange} value={this.state.form && this.state.form.pais} />
                            <br />
                            <label>Idioma: </label>
                            <br />
                            <input type="text" className="form-control" name="idioma" onChange={this.handleChange} value={this.state.form && this.state.form.idioma} />
                            <br />
                            <label>Cantidad de Suscriptores (millones): </label>
                            <br />
                            <input type="text" className="form-control" name="suscriptores" onChange={this.handleChange} value={this.state.form && this.state.form.suscriptores} />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-primary" onClick={() => this.peticionPut()}>Editar</button>{"   "}
                        <button className="btn btn-danger" onClick={() => this.setState({ modalEditar: false })}>Cancelar</button>
                    </ModalFooter>
                </Modal>
            </Container>
        </div>
    );
}


export default Product;
