import React, { useState, useEffect } from 'react';
import { Collapse, Container, Navbar, NavbarBrand, NavbarToggler, NavItem, NavLink } from 'reactstrap';
import { Link } from 'react-router-dom';
import '../css/NavMenu.css';
import logo from '../logo.svg';
/*import Cookies from 'universal-cookie';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPowerOff } from '@fortawesome/free-solid-svg-icons'
import configData from "../configuration.json";
import { useIdleTimer } from 'react-idle-timer'
import logo from '../assets/Logo.png';
import axios from 'axios';*/

function NavMenu() {

    //const baseUrl = `${configData.SERVER_URL}Home/`;

    const [collapsed, setCollapsed] = useState(true);;

    const onToggleNavbar = e => {
        setCollapsed(!collapsed);
    }

    //const cookies = new Cookies();
    //const history = useHistory();

    const cerrarSesion = () => {
        /*await axios.post(`${baseUrl}SignOut`)
            .then((result) => {

                if (result.data) {
                    if (result.data.Valido) {
                        cookies.remove('id', { path: '/' });
                        history.push('/')
                        return;
                    }
                    throw result.data.Message
                }

                throw new Error("Ocurrió un problema procesando el cierre de sesión");

            }).catch((error) => {
                console.log(error);
                history.push('/')
            });

        cookies.remove('id', { path: '/' });
        history.push('/');*/
    }

   /* const handleOnIdle = event => {
        getLastActiveTime();
        cerrarSesion();
    }

    const handleOnActive = event => { getRemainingTime()    }

    const handleOnAction = (e) => { }

    const { getRemainingTime, getLastActiveTime } = useIdleTimer({
        timeout: 1000 * 60 * (configData.TIEMPO_COOKIE_SEG / 60),
        onIdle: handleOnIdle,
        onActive: handleOnActive,
        onAction: handleOnAction,
        debounce: 500
    });*/


    useEffect(() => {
       /* if (!cookies.get('id')) {
            history.push("/");
        }*/

        window.addEventListener("beforeunload", (ev) => {
            //cerrarSesion();
            //ev.preventDefault();
            //return this.doSomethingBeforeUnload();

        });
        //window.addEventListener('unload', cerrarSesion)

    }, []); // eslint-disable-line react-hooks/exhaustive-deps


    return (
        <header className="color_special_site" >
            <Navbar className="navbar-expand-sm navbar-toggleable-sm ng-white border-bottom box-shadow mb-3" light>
                <Container>
                    <NavbarBrand tag={Link} to="/">
                        <img src={logo} alt="logo" height={40} /> Prueba :)
                    </NavbarBrand>
                    <NavbarToggler onClick={onToggleNavbar} className="mr-2" />
                    <Collapse className="d-sm-inline-flex flex-sm-row-reverse" isOpen={!collapsed} navbar>
                        <ul className="navbar-nav flex-grow nav_to_end">
                            <NavItem>
                                <NavLink tag={Link} className="text-secondary" to="/product">Productos</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink tag={Link} className="text-secondary" to="/order">Ordenes</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink tag={Link} className="text-secondary" to="/user">Usuarios</NavLink>
                            </NavItem>
                        </ul>
                    </Collapse>
                </Container>
            </Navbar>
        </header>
    );

}

export default NavMenu;
