import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom";
import DataTable from 'react-data-table-component';
import { Button, Container, Label, Modal, ModalHeader, ModalBody, ModalFooter, CustomInput, Tooltip } from "reactstrap";
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faBars } from '@fortawesome/free-solid-svg-icons'
import FilterComponent from '../../components/InputBusquedaDT';
import { paginationOptions, paginationPerPage, paginationRowsPerPageOptions, fixedHeaderScrollHeight }
    from '../../components/DataTableConfig';
import Cookies from 'universal-cookie';
import configData from "../../configuration.json";
import LoadSpinner from '../../components/LoadSpinner';
import ToastMessage from '../../components/ToastMessage';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const Order = () => {
    const baseUrl = `${configData.SERVER_URL}Conciliacion/`;
    const cookies = new Cookies();
    const history = useHistory();
    const currentDate = new Date();


    const mensajeVacio = {
        show: false,
        icon: "white",
        title: "",
        message: "",
        position: "10%"
    }
    const [mensaje, setMensaje] = useState(mensajeVacio);

    const closeMensaje = () => {
        setTimeout(() => {
            setMensaje(mensajeVacio)
        }, 3000);
    };
    const encabezadoAsiento = {
        Asiento_Id: 0,
        Id_Asiento: 0,
        Tipo_Asiento: '',
        Asiento_Estado: '',
        Asiento_Debito_Total: 0,
        Asiento_Credito_Total: 0,
        Fecha_Asiento: '',
        Asiento_Procesado_Texto: '',
        Proceso_Error: '',
        Codigo_Interfaz: 0,
        Detalle: {}
    };

    const detalleAsiento = {
        Asiento_Id: 0,
        Detalle_Id: 0,
        Tipo_Asiento: '',
        Cuenta_Id: '',
        Nombre_Cuenta: '',
        Asiento_Definicion_Detalle_Id: 0,
        DEBCRE: 0,
        Monto: 0,
        Referencia: '',
        CC1: '',
        CC2: '',
        CC3: '',
    };

    const [tooltipOpen, setTooltipOpen] = useState(false);
    const [dataAsientoEncabezado, setDataAsientoEncabezado] = useState([encabezadoAsiento]);
    const [dataAsientoDetalle, setDataAsientoDetalle] = useState([detalleAsiento]);
    const [modalMostrar, setModalMostrar] = useState(false);
    const [loading, setLoading] = useState(false);

    const [tipos_Asiento, setTipos_Asiento] = useState([]);
    const [fechaDesde, setFechaDesde] = useState(new Date(currentDate.getFullYear(), currentDate.getMonth(), 1));
    const [fechaHasta, setFechaHasta] = useState(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0));
    const [procesado, setProcesado] = useState(true);
    const [filtroTodos, setFiltroTodos] = useState(false);
    const [disableProcesado, setDisableProcesado] = useState(false);
    const toggle = () => setTooltipOpen(!tooltipOpen);

    const formFiltros = {
        FechaDesde: new Date(currentDate.getFullYear(), currentDate.getMonth(), 1),
        FechaHasta: new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0),
        TiposAsiento: "",
        Procesado: true,
        Todos: true
    };

    const [filtroBusqueda, setFiltroBusqueda] = useState(formFiltros);

    const handleFiltroChange = event => {
        const { name, value } = event.target;
        setFiltroBusqueda({ ...filtroBusqueda, [name]: value });
    };

    const handleFiltroFechaHastaChange = fecha => {
        setFechaHasta(fecha);
        setFiltroBusqueda({
            ...filtroBusqueda, 'FechaHasta': fecha
        });
    };

    const handleFiltroFechaDesdeChange = fecha => {
        setFechaDesde(fecha);
        setFiltroBusqueda({
            ...filtroBusqueda, 'FechaDesde': fecha
        });
    };

    const filtrarTodosAsientos = () => {
        setFiltroTodos(!filtroTodos);
        filtroBusqueda.TiposAsiento = "";
        setDisableProcesado(!filtroTodos);

    };

    const filtrarAsientos = (e) => {
        e.preventDefault();

        if (!filtroBusqueda.FechaDesde || !filtroBusqueda.FechaHasta) {
            setMensaje({ show: true, icon: "warning", title: "Advertencia", message: "Defina el rango de fecha para procesar la búsqueda", position: "10%" });
            closeMensaje();
            return;
        }

        if (filtroBusqueda.FechaDesde > filtroBusqueda.FechaHasta) {
            setMensaje({ show: true, icon: "warning", title: "Advertencia", message: "Rango de fecha incorrecto", position: "10%" });
            closeMensaje();
            return;
        }

        var fDesde = padLeft(filtroBusqueda.FechaDesde.getDate()) + "/" + padLeft(parseInt(filtroBusqueda.FechaDesde.getMonth()) + 1) + "/" + filtroBusqueda.FechaDesde.getFullYear();
        var fHasta = padLeft(filtroBusqueda.FechaHasta.getDate()) + "/" + padLeft(parseInt(filtroBusqueda.FechaHasta.getMonth()) + 1) + "/" + filtroBusqueda.FechaHasta.getFullYear();

        setLoading(true);

        var nuevoFiltro = {
            FechaDesde: fDesde,
            FechaHasta: fHasta,
            TiposAsiento: filtroBusqueda.TiposAsiento,
            Procesado: procesado,
            Todos: filtroTodos
        };

        obtieneAsientosSAP(nuevoFiltro);
    };


    const accionesParametrizacion = (row) => {
        setDataAsientoDetalle(row.Detalle);
        abrirCerrarModalMostrar();
    }

    const columns = [
        {
            name: 'Ver',
            button: true,
            cell: (row) => <Button className="btn btn-success" onClick={() => accionesParametrizacion(row)}><FontAwesomeIcon icon={faBars} /></Button>
        },
        {
            name: 'Asiento Id',
            selector: 'Asiento_Id',
            sortable: true,
            width: '110px',
            right: true
        },
        {
            name: 'Tipo',
            selector: 'Tipo_Asiento',
            sortable: true,
            left: true,
        },
        {
            name: 'Estado',
            selector: 'Asiento_Estado',
            sortable: true,
            left: true,
        },
        {
            name: 'Débito',
            selector: 'Asiento_Debito_Total',
            sortable: true,
            right: true,
        },
        {
            name: 'Crédito',
            selector: 'Asiento_Credito_Total',
            sortable: true,
            right: true,
        },
        {
            name: 'Procesado',
            selector: 'Asiento_Procesado_Texto',
            sortable: true,
            center: true,
        },
        {
            name: 'Cód Interfaz',
            selector: 'Codigo_Interfaz',
            sortable: true,
            right: true,
            width: '120px'
        },
        {
            name: 'Fecha',
            selector: 'Fecha_Asiento_Formato',
            sortable: true,
            left: true,
        },
        {
            name: 'Msg',
            selector: 'Proceso_Error',
            sortable: true,
            left: true,
            width: '300px'
        }
    ];

    const abrirCerrarModalMostrar = () => {
        setModalMostrar(!modalMostrar);
    };

    //Hooks & Functions for Input Filter
    const [filterText, setFilterText] = useState('');
    const [resetPaginationToggle, setResetPaginationToggle] = useState(false);
    const filteredItems = (filterText === '') ? dataAsientoEncabezado : dataAsientoEncabezado.filter(item => (item.Asiento_Id && item.Asiento_Id.toString().toLowerCase().includes(filterText.toLowerCase()))
        || (item.Codigo_Interfaz && item.Codigo_Interfaz.toString().toLowerCase().includes(filterText.toLowerCase()))
    );

    const subHeaderComponentMemo = React.useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setResetPaginationToggle(!resetPaginationToggle);
                setFilterText('');
            }
        };

        return <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} placeholder={"Filtrar Cód. Interfaz"} />;
    }, [filterText, resetPaginationToggle]);

    const padLeft = (n) => {
        return ("00" + n).slice(-2);
    }

    const obtieneAsientosSAP = (filtros) => {
        const querystring = require('querystring');
        axios.request({
            url: `${baseUrl}GetAsientosSAP?` + querystring.stringify(filtros),
            method: 'get',
            data: filtros
        })
            .then((result) => {

                if (result.data) {
                    if (result.data.Success) {
                        setDataAsientoEncabezado(result.data.Asiento_SAP_Encabezado);
                        setLoading(false);
                        return;
                    }

                    throw new Error(result.data.Message);
                }

                throw new Error("Ocurrió un problema consultando los Asientos");

            }).catch((error) => {
                if (error.request.responseURL.includes("ReturnUrl")) {
                    cookies.remove('id', { path: '/' });
                    history.push('/');
                    return;
                }
                setLoading(false);
                setMensaje({ show: true, icon: "danger", title: "Error", message: "Ocurrió un problema consultando los Asientos", position: "10%" });
                closeMensaje();
            });
    };

    useEffect(() => {

        const obtieneCatalogos = async () => {
            await axios.get(`${baseUrl}GetTipoAsientoSAP`)
                .then((result) => {

                    if (result.data) {
                        if (result.data.Success) {
                            setTipos_Asiento(result.data.Tipos_Asientos);
                            return;
                        }
                        throw new Error(result.data.Message);
                    }

                    throw new Error("Ocurrió un problema consultando los catálogos");

                }).catch((error) => {
                    if (error.request.responseURL.includes("ReturnUrl")) {
                        cookies.remove('id', { path: '/' });
                        history.push('/');
                        return;
                    }
                    setLoading(false);
                    setMensaje({ show: true, icon: "danger", title: "Error", message: "Ocurrió un problema consultando el catálogo de Tipos de Asiento", position: "10%" });
                    closeMensaje();
                });
        }

        if (cookies.get('id')) {
            setLoading(true);
            //Filtra todos en el mes
            var fDesde = padLeft(filtroBusqueda.FechaDesde.getDate()) + "/" + padLeft(parseInt(filtroBusqueda.FechaDesde.getMonth()) + 1) + "/" + filtroBusqueda.FechaDesde.getFullYear();
            var fHasta = padLeft(filtroBusqueda.FechaHasta.getDate()) + "/" + padLeft(parseInt(filtroBusqueda.FechaHasta.getMonth()) + 1) + "/" + filtroBusqueda.FechaHasta.getFullYear();

            var filtros = {
                FechaDesde: fDesde,
                FechaHasta: fHasta,
                Estado: '',
                Todos: true
            };
            obtieneCatalogos();
            obtieneAsientosSAP(filtros);
        }
    }, []);// eslint-disable-line react-hooks/exhaustive-deps


    return (
        <div>
            {loading ? <LoadSpinner Mensaje="Cargando, Por Favor Espere" /> : ""}
            <ToastMessage message={mensaje.message} title={mensaje.title} show={mensaje.show} icon={mensaje.icon} position={mensaje.position} />
            <Container>
                <div className="">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb">
                            <li className="breadcrumb-item text-primary"><a href="/#" onClick={ev => { ev.preventDefault(); history.push(`/home`) }} style={{ "cursor": "pointer" }}>Dashboard</a></li>
                            <li className="breadcrumb-item active" aria-current="page">Conciliación</li>
                        </ol>
                    </nav>
                    <h3 className="text-primary">Conciliación de Asientos Contables SAP</h3>
                </div>
                <form className="mt-4">
                    <fieldset className="form-group fieldset-custom">
                        <div className="row">
                            <legend className="col-form-label col-sm pt-1 text-primary">Filtros de Búsqueda</legend>
                        </div>
                        <div className="row">
                            <div className="col-sm-2 mb-1">
                                <Label className="col-lg">F. Desde</Label>
                                <DatePicker className="col-lg form-control" name="FechaDesde" id="FechaDesde" dateFormat="dd/MM/yyyy" selected={fechaDesde} onChange={handleFiltroFechaDesdeChange} />
                            </div>
                            <div className="col-sm-2 mb-1">
                                <Label className="col-lg">F. Hasta</Label>
                                <DatePicker className="col-lg form-control" name="FechaHasta" id="FechaHasta" dateFormat="dd/MM/yyyy" selected={fechaHasta} onChange={handleFiltroFechaHastaChange} />
                            </div>
                            <div className="col-sm-2 mb-1">
                                <label htmlFor="inputState">Tipo Asiento</label>
                                <select id="inputState" className="form-control" name="TiposAsiento" value={filtroBusqueda.TiposAsiento || ""} onChange={handleFiltroChange} disabled={disableProcesado}>
                                    <option value="">Todos</option>
                                    {tipos_Asiento.map((tipo, i) =>
                                        <option key={i} value={tipo.Codigo}>
                                            {tipo.Nombre}
                                        </option>
                                    )}
                                </select>

                            </div>
                            <div className="col-sm-2 mb-1" style={{ "marginTop": "35px" }}>
                                <CustomInput type="switch" id="switchProcesados" name="customSwitch" label="Procesados" checked={procesado} onChange={() => setProcesado(!procesado)} disabled={disableProcesado} />
                            </div>
                            <div className="col-sm-2 mb-1" style={{ "marginTop": "35px" }}>
                                <CustomInput type="switch" id="switchTodos" name="customSwitch" label="Todos" checked={filtroTodos} onChange={filtrarTodosAsientos} />
                            </div>
                            <div className="col-sm-2 mb-1">
                                <Button variant="primary" type="submit" id={"btnSearchFilter"} className="btn-primary_override float-right" onClick={filtrarAsientos} style={{ "marginTop": "29px" }}>
                                    <FontAwesomeIcon icon={faSearch} />
                                </Button>
                                <Tooltip
                                    placement={"left"}
                                    isOpen={tooltipOpen}
                                    target={"btnSearchFilter"}
                                    toggle={toggle}>
                                    Buscar
                                </Tooltip>
                            </div>
                        </div>
                    </fieldset>
                </form>
                <div className="table-responsive">
                    <DataTable
                        columns={columns}
                        data={filteredItems}
                        pagination
                        paginationResetDefaultPage={resetPaginationToggle}
                        subHeader
                        subHeaderComponent={subHeaderComponentMemo}
                        persistTableHead


                        paginationComponentOptions={paginationOptions}
                        fixedHeader
                        fixedHeaderScrollHeight={fixedHeaderScrollHeight}
                        noHeader={true}
                        paginationPerPage={paginationPerPage}
                        paginationRowsPerPageOptions={paginationRowsPerPageOptions}
                        noDataComponent={paginationOptions.noDataComponent}
                    />
                </div>
                <Modal isOpen={modalMostrar}>
                    <ModalHeader>
                        Detalle del Asiento {dataAsientoDetalle && dataAsientoDetalle[0].Asiento_Id}
                    </ModalHeader>
                    <ModalBody>
                        <div className="table-responsive detalle-cuenta">
                            <table className='table table-striped' aria-labelledby="tabelLabel">
                                <thead>
                                    <tr>
                                        <th>Nombre Cuenta</th>
                                        <th>Referencia</th>
                                        <th>Cuenta Id</th>
                                        <th>Débito</th>
                                        <th>Crédito</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {dataAsientoDetalle.map((detalle, i) =>
                                        <tr key={i}>
                                            <td>{detalle.Nombre_Cuenta}</td>
                                            <td>{detalle.Referencia}</td>
                                            <td>{detalle.Cuenta_Id}</td>
                                            <td>{detalle.DEBCRE === 'DEB' ? detalle.Monto : 0}</td>
                                            <td>{detalle.DEBCRE === 'CRE' ? detalle.Monto : 0}</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-secondary" onClick={() => abrirCerrarModalMostrar()}>Cerrar</button>
                    </ModalFooter>
                </Modal>
            </Container>
        </div>
    );
}


export default Order;
