import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom";
import firebase from "./firebase";
import DataTable from 'react-data-table-component';
import { Button, Container, Label, Modal, ModalHeader, ModalBody, ModalFooter, CustomInput, Tooltip } from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faBars } from '@fortawesome/free-solid-svg-icons'
import FilterComponent from '../../components/InputBusquedaDT';
import LoadSpinner from '../../components/LoadSpinner';
import { paginationOptions, paginationPerPage, paginationRowsPerPageOptions, fixedHeaderScrollHeight }
    from '../../components/DataTableConfig';
//import Cookies from 'universal-cookie';
import ToastMessage from '../../components/ToastMessage';


const Product = () => {
    //const baseUrl = `${configData.SERVER_URL}Conciliacion/`;
    //const cookies = new Cookies();
    const history = useHistory();
    //const currentDate = new Date();


    const mensajeVacio = {
        show: false,
        icon: "white",
        title: "",
        message: "",
        position: "10%"
    }
    const [mensaje, setMensaje] = useState(mensajeVacio);

    const closeMensaje = () => {
        setTimeout(() => {
            setMensaje(mensajeVacio)
        }, 3000);
    };
    const encabezadoProducto = {
        Codigo_Producto: 0,
        Fecha_Registro: new Date(),
        Cantidad_Existencia: 0,
        Nombre_Producto: 0,
        Detalle: {}
    };

    const detalleOrden = {
        Detalle_Id: 0,
        Codigo_Producto: 0,
        Articulo_Id: 0,
        Cantidad: 0,
        Total: 0
    };

    const [dataProducto, setDataProducto] = useState([encabezadoProducto]);
    const [dataShowProducto, setDataShowProducto] = useState([encabezadoProducto]);

    const [modalMostrar, setModalMostrar] = useState(false);
    const [loading, setLoading] = useState(false);

    const accionesOrdenes = (row) => {
        setDataShowProducto(row);
        abrirCerrarModalMostrar();
    }

    const columns = [
        {
            name: 'Ver',
            button: true,
            cell: (row) => <Button className="btn btn-success" onClick={() => accionesOrdenes(row)}><FontAwesomeIcon icon={faBars} /></Button>
        },
        {
            name: 'Número Orden',
            selector: 'Codigo_Producto',
            sortable: true,
            width: '110px',
            right: true
        },
        {
            name: 'Fecha',
            selector: 'Fecha_Registro',
            sortable: true,
            left: true,
        },
        {
            name: 'Total Orden',
            selector: 'Cantidad_Existencia',
            sortable: true,
            left: true,
        },
        {
            name: 'Nombre Cliente',
            selector: 'Nombre_Producto',
            sortable: true,
            left: true,
            width: '300px'
        }
    ];

    const abrirCerrarModalMostrar = () => {
        setModalMostrar(!modalMostrar);
    };

    //Hooks & Functions for Input Filter
    const [filterText, setFilterText] = useState('');
    const [resetPaginationToggle, setResetPaginationToggle] = useState(false);
    const filteredItems = (filterText === '') ? dataProducto : dataProducto.filter(item => (item.Asiento_Id && item.Asiento_Id.toString().toLowerCase().includes(filterText.toLowerCase()))
        || (item.Codigo_Interfaz && item.Codigo_Interfaz.toString().toLowerCase().includes(filterText.toLowerCase()))
    );

    const subHeaderComponentMemo = React.useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setResetPaginationToggle(!resetPaginationToggle);
                setFilterText('');
            }
        };

        return <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} placeholder={"Cód. Producto"} />;
    }, [filterText, resetPaginationToggle]);

    peticionGet = () => {
        firebase.child("canales").on("value", (canal) => {
          if (canal.val() !== null) {
            this.setState({ ...this.state.data, data: canal.val() });
          } else {
            this.setState({ data: [] });
          }
        });
      };

    useEffect(() => {
        /*if (cookies.get('id')) {
        }*/
    }, []);// eslint-disable-line react-hooks/exhaustive-deps


    return (
        <div>
            {loading ? <LoadSpinner Mensaje="Cargando, Por Favor Espere" /> : ""}
            <ToastMessage message={mensaje.message} title={mensaje.title} show={mensaje.show} icon={mensaje.icon} position={mensaje.position} />
            <Container>
                <div className="">
                    <h3 className="text-primary">Lista de Productos</h3>
                </div>
                <div className="table-responsive">
                    <DataTable
                        columns={columns}
                        data={filteredItems}
                        pagination
                        paginationResetDefaultPage={resetPaginationToggle}
                        subHeader
                        subHeaderComponent={subHeaderComponentMemo}
                        persistTableHead


                        paginationComponentOptions={paginationOptions}
                        fixedHeader
                        fixedHeaderScrollHeight={fixedHeaderScrollHeight}
                        noHeader={true}
                        paginationPerPage={paginationPerPage}
                        paginationRowsPerPageOptions={paginationRowsPerPageOptions}
                        noDataComponent={paginationOptions.noDataComponent}
                    />
                </div>
                <Modal isOpen={modalMostrar}>
                    <ModalHeader>
                        Detalle de la orden {dataDetalleOrdenes && dataDetalleOrdenes[0].Codigo_Producto}
                    </ModalHeader>
                    <ModalBody>
                        <div className="table-responsive detalle-cuenta">
                            <table className='table table-striped' aria-labelledby="tabelLabel">
                                <thead>
                                    <tr>
                                        <th>Detalle Id</th>
                                        <th>Núm Orden</th>
                                        <th>Código Artículo</th>
                                        <th>Cantidad</th>
                                        <th>Total</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {dataDetalleOrdenes.map((detalle, i) =>
                                        <tr key={i}>
                                            <td>{detalle.Detalle_Id}</td>
                                            <td>{detalle.Codigo_Producto}</td>
                                            <td>{detalle.Articulo_Id}</td>
                                            <td>{detalle.Cantidad}</td>
                                            <td>{detalle.Total}</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-secondary" onClick={() => abrirCerrarModalMostrar()}>Cerrar</button>
                    </ModalFooter>
                </Modal>
            </Container>
        </div>
    );
}


export default Product;
