      lastIdle.current = (+new Date()) - _timeout.current
      emitOnIdle.current(e)
    } else {
      if (!stopOnIdle) {
        idleTime.current += (+new Date()) - lastIdle.current
        _bindEvents()
        emitOnActive.current(e)
      }
