    if (tId.current !== null) reset()
    firstLoad.current = false
  }, [timeout])

  return {
    isIdle,
    pause,
    reset,
    resume,
    getLastIdleTime,
    getTotalIdleTime,
    getLastActiveTime,
    getTotalActiveTime,
    getElapsedTime,
    getRemainingTime
  }
