import React, { useState, useEffect } from 'react';
import { fireAuth as firebaseAuth } from "../../firebase";
import DataTable from 'react-data-table-component';
import { Button, Container, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faPen } from '@fortawesome/free-solid-svg-icons'
import FilterComponent from '../../components/InputBusquedaDT';
import LoadSpinner from '../../components/LoadSpinner';
import { paginationOptions, paginationPerPage, paginationRowsPerPageOptions, fixedHeaderScrollHeight }
    from '../../components/DataTableConfig';
//import Cookies from 'universal-cookie';
import ToastMessage from '../../components/ToastMessage';


const User = () => {
    //const cookies = new Cookies();
    const user_init = {
        Id: 0,
        Correo: '',
        Contrasena: '',
        ConfirmaContrasena: '',
        Fecha: ''
    };
    const [dataUsers, setDataUsers] = useState([user_init]);
    const [dataUserForm, setDataUserForm] = useState(user_init);
    const [modalInsertar, setModalInsertar] = useState(false);
    const [editar, setEditar] = useState(false);
    const [loading, setLoading] = useState(false);
    const mensajeVacio = { show: false, icon: "white", title: "", message: "", position: "10%" }
    const [mensaje, setMensaje] = useState(mensajeVacio);

    const closeMensaje = () => { setTimeout(() => { setMensaje(mensajeVacio) }, 3000); };

    const editarUsuario = (row) => {
        setDataUserForm(row);
        setEditar(true);
        setModalInsertar(!modalInsertar);
    }

    const columns = [
        {
            name: 'Id',
            selector: 'Id',
            sortable: true
        },
        {
            name: 'Correo',
            selector: 'Correo',
            sortable: true,
            left: true,
        },
        {
            name: 'Fecha Registro',
            selector: 'Fecha',
            sortable: true,
            width: '200px',
            left: true,
        },
        {
            name: 'Editar',
            button: true,
            cell: (row) => <Button className="btn btn-success" onClick={() => editarUsuario(row, "editar")}><FontAwesomeIcon icon={faPen} /></Button>
        },
    ];


    const abrirCerrarModalInsertar = () => {
        setEditar(false);
        setDataUserForm(user_init);
        setModalInsertar(!modalInsertar);
    };

    //Funciones y Hooks para el campo de Filtro
    const [filterText, setFilterText] = useState('');
    const [resetPaginationToggle, setResetPaginationToggle] = useState(false);
    const filteredItems = (filterText === '') ? dataUsers : dataUsers.filter(item => (item.Correo && item.Correo.toString().toLowerCase().includes(filterText.toLowerCase())));

    const subHeaderComponentMemo = React.useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setResetPaginationToggle(!resetPaginationToggle);
                setFilterText('');
            }
        };
        return <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} placeholder={"Correo"} />;
    }, [filterText, resetPaginationToggle]);


    //Funciones Handle
    const handleFormUserChange = event => {
        const { name, value } = event.target;
        setDataUserForm({ ...dataUserForm, [name]: value });
    };

    //Funciones del CRUD
    const obtenerUsuarios = () => {
        setLoading(true);
        /*firebase.child("producto").on("value", (producto) => {
             if (producto.val() !== null) {
                 var list = new Array ();
                 
                 producto.forEach (function (data) {
                     var item = {
                       Id: data.key,
                       Codigo_Producto: data.val ().Codigo_Producto,
                       Cantidad_Existencia: data.val ().Cantidad_Existencia,
                       Nombre_Producto: data.val ().Nombre_Producto,
                       Descripcion_Producto: data.val ().Descripcion_Producto,
                       Precio: data.val().Precio
                     }
                     list.push (item);
                   });
 
                 setDataProducto(list);
             } else {
                 setDataProducto(encabezadoProducto);
             }
             setTimeout(() => { setLoading(false) }, 200);
             
         });*/
        /*firebaseAuth.onAuthStateChanged(userAuth => {
            var user = userAuth;
        });*/
        firebaseAuth.paginationResetDefaultPage
            .then((getUsersResult) => {
                var user = getUsersResult;
            })
            .catch((error) => { });
        setLoading(false);
        setDataUsers(user_init);
    };

    const guardarUsuario = () => {

        if (!validacionForm()) return;

        firebaseAuth.createUserWithEmailAndPassword(dataUserForm.Correo, dataUserForm.Contrasena)
            .then(() => {
                setMensaje({ show: true, icon: "success", title: "Información", message: "Registrado Correctamente", position: "10%" });
                closeMensaje();
            })
            .catch((err) => {
                setMensaje({ show: true, icon: "danger", title: "Error", message: `Ocurrió un problema procesando la actualización: ${err}`, position: "10%" });
                closeMensaje();
            })

        abrirCerrarModalInsertar();
    };

    const actualizarUsuario = () => {

        if (!validacionForm) return;

        //TODOOO

        abrirCerrarModalInsertar();
    };

    const validacionForm = () => {

        if (!dataUserForm.Correo || !dataUserForm.Contrasena) {
            setMensaje({ show: true, icon: "danger", title: "Error", message: "Por Favor defina un correo y una contraseña", position: "10%" });
            closeMensaje();
            return false;
        }
        if (dataUserForm.Contrasena.length < 6) {
            setMensaje({ show: true, icon: "danger", title: "Error", message: "la contraseña debe tener 6 o mas carácteres", position: "10%" });
            closeMensaje();
            return false;
        }

        if (dataUserForm.ConfirmaContrasena !== dataUserForm.Contrasena) {
            setMensaje({ show: true, icon: "danger", title: "Error", message: "la contraseña y la confirmación de contraseña no coinciden", position: "10%" });
            closeMensaje();
            return false;
        }

        return true;
    }


    useEffect(() => {
        obtenerUsuarios();
        /*if (cookies.get('id')) {
        }*/
    }, []);// eslint-disable-line react-hooks/exhaustive-deps


    return (
        <div>
            {loading ? <LoadSpinner Mensaje="Cargando, Por Favor Espere" /> : ""}
            <ToastMessage message={mensaje.message} title={mensaje.title} show={mensaje.show} icon={mensaje.icon} position={mensaje.position} />
            <Container>
                <div className="">
                    <h3 className="text-primary">Registro de Usuarios</h3>
                    <hr></hr>
                    <button className="btn btn-success mt-3" onClick={() => abrirCerrarModalInsertar()}><FontAwesomeIcon icon={faPlus} /> Registrar</button>
                </div>
                <form>
                    <div className="form-group">
                        <label>Correo: </label>
                        <br />
                        <input type="email" className="form-control" autoComplete="off" name="Correo" value={dataUserForm && dataUserForm.Correo} onChange={handleFormUserChange} disabled={editar} required />
                        <br />
                        <label>Contraseña: </label>
                        <br />
                        <input type="password" className="form-control" autoComplete="off" name="Contrasena" value={dataUserForm && dataUserForm.Contrasena} onChange={handleFormUserChange} />
                        <br />
                        <label>Confirme Contraseña: </label>
                        <br />
                        <input type="password" className="form-control" autoComplete="off" name="ConfirmaContrasena" value={dataUserForm && dataUserForm.ConfirmaContrasena} onChange={handleFormUserChange} />
                        <br />
                    </div>
                    <div>
                        <button className="btn btn-primary" onClick={() => !editar ? guardarUsuario() : actualizarUsuario()}>{!editar ? "Guardar" : "Actualizar"}</button>{"   "}
                        <button className="btn btn-danger" onClick={() => abrirCerrarModalInsertar()}>Cancelar</button>
                    </div>

                </form>
                <div className="table-responsive">
                    <DataTable
                        columns={columns}
                        data={filteredItems}
                        pagination
                        paginationResetDefaultPage={resetPaginationToggle}
                        subHeader
                        subHeaderComponent={subHeaderComponentMemo}
                        persistTableHeadpaginationComponentOptions={paginationOptions}
                        fixedHeader
                        fixedHeaderScrollHeight={fixedHeaderScrollHeight}
                        noHeader={true}
                        paginationPerPage={paginationPerPage}
                        paginationRowsPerPageOptions={paginationRowsPerPageOptions}
                        noDataComponent={paginationOptions.noDataComponent}
                    />
                </div>
                <Modal isOpen={modalInsertar}>
                    <ModalHeader>{!editar ? "Insertar" : "Actualizar"} Usuario</ModalHeader>
                    <ModalBody>

                    </ModalBody>
                    <ModalFooter>

                    </ModalFooter>
                </Modal>
            </Container>
        </div>
    );
}


export default User;
