import React, { useState, useEffect } from 'react';
import { Button, Form, FormGroup, Label, Container } from 'reactstrap';
//import Cookies from 'universal-cookie';
//import axios from 'axios';
//import LoadSpinner from '../../components/LoadSpinner';
//import configData from "../../configuration.json";

function Login(props) {

    //const baseUrl = `${configData.SERVER_URL}Login/`;
    //const cookies = new Cookies();

    const [form, setForm] = useState({
        Usuario: '',
        Clave: '',
        Mensaje: ''
    });

    //const [loading, setLoading] = useState(false);
    const [mensaje, setMensaje] = useState("");

    const handleChange = e => {
        const { name, value } = e.target;
        setForm({ ...form, [name]: value });
    }

    const iniciarSesion = (e) => {
        e.preventDefault();

        if (!form.Usuario || !form.Clave) {
            setMensaje("Digite un usuario y una contraseña");
            return;
        }
        props.history.push('/Product');

/*
        setLoading(true);
        axios.post(`${baseUrl}ValidaLogin`, form)
            .then((result) => {

                if (result.data) {
                    if (result.data.Valido) {
                        cookies.set('id', result.data.Usuario, { path: '/'});
                        setLoading(false);
                        props.history.push('/home');
                        return;
                    }
                    throw result.data.Mensaje
                }

                throw new Error("Ocurrió un problema consultando las credenciales");

            }).catch((error) => {
                setLoading(false);
                setMensaje(error)
            });*/
    }

    useEffect(() => {
        /*if (cookies.get('id')) {
            props.history.push('/home');
        } else {
            axios.get(`${baseUrl}WakeUpServ`);
        }*/
    }, []);// eslint-disable-line react-hooks/exhaustive-deps

    return (
        <div>
            <Container>
                <Form className="inicioSesion color_special_site">

                    <FormGroup>
                        <Label for="exampleEmail">Usuario</Label>
                        <input
                            type="text"
                            className="form-control"
                            name="Usuario"
                            autoComplete="off"
                            onChange={handleChange}
                        />
                    </FormGroup>

                    <FormGroup>
                        <Label for="examplePassword">Contraseña</Label>
                        <input
                            type="password"
                            className="form-control"
                            name="Clave"
                            onChange={handleChange} />
                    </FormGroup>

                    <p className="text-danger">{mensaje}</p>
                    <br />

                    <Button variant="primary" type="submit" className="btn-primary_override float-right" onClick={iniciarSesion}>
                        Iniciar Sesión
                    </Button>
                  
                </Form>
            </Container>
        </div>
    );
}

export default Login;
