import React, { useState, useEffect } from 'react';
import { fireDefault as firebase } from "../../firebase";
import DataTable from 'react-data-table-component';
import { Button, Container, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faBars, faPen, faTrash } from '@fortawesome/free-solid-svg-icons'
import FilterComponent from '../../components/InputBusquedaDT';
import LoadSpinner from '../../components/LoadSpinner';
import { paginationOptions, paginationPerPage, paginationRowsPerPageOptions, fixedHeaderScrollHeight }
    from '../../components/DataTableConfig';
import ToastMessage from '../../components/ToastMessage';


const Client = () => {

    const clienteInit = {
        Id: 0,
        Nombre_Cliente: '',
        Direccion_Cliente: '',
        Telefono_Cliente: ''
    };

    const [dataCliente, setDataCliente] = useState([clienteInit]);
    const [dataShowCliente, setDataShowCliente] = useState([clienteInit]);
    const [dataClienteForm, setDataClienteForm] = useState(clienteInit);
    const [modalMostrar, setModalMostrar] = useState(false);
    const [modalInsertar, setModalInsertar] = useState(false);
    const [editar, setEditar] = useState(false);
    const [loading, setLoading] = useState(false);
    const mensajeVacio = { show: false, icon: "white", title: "", message: "", position: "10%" }
    const [mensaje, setMensaje] = useState(mensajeVacio);

    const closeMensaje = () => { setTimeout(() => { setMensaje(mensajeVacio) }, 3000); };

    const accionesCliente = (row, accion) => {

        if (accion) {
            switch (accion) {
                case "ver":
                    setDataShowCliente(row);
                    abrirCerrarModalMostrar();
                    break;
                case "editar":
                    setDataClienteForm(row);
                    setEditar(true);
                    setModalInsertar(!modalInsertar);
                    break;
                case "eliminar":
                    eliminarCliente(row);
                    break;
                default:
                    break;
            }
        }
    }

    const columns = [
        {
            name: 'Cód Producto',
            selector: 'Telefono_Cliente',
            sortable: true
        },
        {
            name: 'Nombre Producto',
            selector: 'Nombre_Cliente',
            sortable: true,
            width: '200px',
            left: true,
        },
        {
            name: 'Descripción Producto',
            selector: 'Direccion_Cliente',
            sortable: true,
            width: '300px',
            left: true,
        },
        {
            name: 'Ver',
            button: true,
            cell: (row) => <Button className="btn btn-primary" onClick={() => accionesCliente(row, "ver")}><FontAwesomeIcon icon={faBars} /></Button>
        },
        {
            name: 'Editar',
            button: true,
            cell: (row) => <Button className="btn btn-success" onClick={() => accionesCliente(row, "editar")}><FontAwesomeIcon icon={faPen} /></Button>
        },
        {
            name: 'Eliminar',
            button: true,
            cell: (row) => <Button className="btn btn-danger" onClick={() => accionesCliente(row, "eliminar")}><FontAwesomeIcon icon={faTrash} /></Button>
        },
    ];

    const abrirCerrarModalMostrar = () => { setModalMostrar(!modalMostrar); };
    const abrirCerrarModalInsertar = () => {
        setEditar(false);
        setDataClienteForm(clienteInit);
        setModalInsertar(!modalInsertar);
    };

    //Funciones y Hooks para el campo de Filtro
    const [filterText, setFilterText] = useState('');
    const [resetPaginationToggle, setResetPaginationToggle] = useState(false);
    const filteredItems = (filterText === '') ? dataCliente : dataCliente.filter(item => (item.Telefono_Cliente && item.Telefono_Cliente.toString().toLowerCase().includes(filterText.toLowerCase())));

    const subHeaderComponentMemo = React.useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setResetPaginationToggle(!resetPaginationToggle);
                setFilterText('');
            }
        };
        return <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} placeholder={"Cód. Producto"} />;
    }, [filterText, resetPaginationToggle]);


    //Funciones Handle
    const handleFormProductoChange = event => {
        const { name, value } = event.target;
        setDataClienteForm({ ...dataClienteForm, [name]: value });
    };

    //Funciones del CRUD
    const obtenerProductos = () => {
        setLoading(true);
        firebase.child("producto").on("value", (producto) => {
            if (producto.val() !== null) {
                var list = [];
                producto.forEach(function (data) {
                    var item = {
                        Id: data.key,
                        Telefono_Cliente: data.val().Telefono_Cliente,
                        Nombre_Cliente: data.val().Nombre_Cliente,
                        Direccion_Cliente: data.val().Direccion_Cliente
                    }
                    list.push(item);
                });

                setDataCliente(list);
            } else {
                setDataCliente(clienteInit);
            }
            setTimeout(() => { setLoading(false) }, 200);

        });
    };

    const guardarProducto = () => {
        if (!dataClienteForm.Telefono_Cliente || !dataClienteForm.Nombre_Cliente) {
            setMensaje({ show: true, icon: "danger", title: "Error", message: "Por Favor defina un código y un nombre para el producto", position: "10%" });
            closeMensaje();
            return;
        }
        firebase.child("producto").push(dataClienteForm,
            error => {
                if (error) {
                    setMensaje({ show: true, icon: "danger", title: "Error", message: "Ocurrió un problema procesando el registro", position: "10%" });
                    closeMensaje();
                }
            });

        abrirCerrarModalInsertar();
    };

    const actualizarProducto = () => {

        if (!validacionForm()) return;

        firebase.child(`producto/${dataClienteForm.Id}`).set(
            dataClienteForm,
            error => {
                if (error) {
                    setMensaje({ show: true, icon: "danger", title: "Error", message: "Ocurrió un problema procesando la actualización", position: "10%" });
                    closeMensaje();
                }
            });

        abrirCerrarModalInsertar();
    };

    const validacionForm = () => {
        if (!dataClienteForm.Id) {
            setMensaje({ show: true, icon: "danger", title: "Error", message: "No es posible actualizar el producto, Id no encontrado", position: "10%" });
            closeMensaje();
            return false;
        }
        if (!dataClienteForm.Telefono_Cliente || !dataClienteForm.Nombre_Cliente) {
            setMensaje({ show: true, icon: "danger", title: "Error", message: "Por Favor defina un código y un nombre para el producto", position: "10%" });
            closeMensaje();
            return false;
        }

        return true;
    }

    const eliminarCliente = (row) => {
        if (window.confirm(`Estás seguro que deseas eliminar el producto ${row && row.Telefono_Cliente}?`)) {
            firebase.child(`producto/${row.Id}`).remove(
                error => {
                    if (error) {
                        setMensaje({ show: true, icon: "danger", title: "Error", message: "Ocurrió un problema procesando la eliminación", position: "10%" });
                        closeMensaje();
                    }
                });
        }
    }

    useEffect(() => {
        obtenerProductos();
    }, []);// eslint-disable-line react-hooks/exhaustive-deps


    return (
        <div>
            {loading ? <LoadSpinner Mensaje="Cargando, Por Favor Espere" /> : ""}
            <ToastMessage message={mensaje.message} title={mensaje.title} show={mensaje.show} icon={mensaje.icon} position={mensaje.position} />
            <Container>
                <div className="">
                    <h3 className="text-primary">Lista de Productos</h3>
                    <hr></hr>
                    <button className="btn btn-success mt-3" onClick={() => abrirCerrarModalInsertar()}><FontAwesomeIcon icon={faPlus} /> Registrar</button>
                </div>
                <div className="table-responsive">
                    <DataTable
                        columns={columns}
                        data={filteredItems}
                        pagination
                        paginationResetDefaultPage={resetPaginationToggle}
                        subHeader
                        subHeaderComponent={subHeaderComponentMemo}
                        persistTableHeadpaginationComponentOptions={paginationOptions}
                        fixedHeader
                        fixedHeaderScrollHeight={fixedHeaderScrollHeight}
                        noHeader={true}
                        paginationPerPage={paginationPerPage}
                        paginationRowsPerPageOptions={paginationRowsPerPageOptions}
                        noDataComponent={paginationOptions.noDataComponent}
                    />
                </div>
                <Modal isOpen={modalMostrar} className="modal-dialog modal-lg">
                    <ModalHeader>
                        Producto N° {dataShowCliente && dataShowCliente.Telefono_Cliente}
                    </ModalHeader>
                    <ModalBody>
                        <div className="table-responsive">
                            <table className='table table-striped' aria-labelledby="tabelLabel">
                                <thead>
                                    <tr>
                                        <th>Código</th>
                                        <th>Nombre</th>
                                        <th>Descripción</th>
                                        <th>Existencia</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>{dataShowCliente.Nombre_Cliente}</td>
                                        <td>{dataShowCliente.Direccion_Cliente}</td>
                                        <td>{dataShowCliente.Telefono_Cliente}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-secondary" onClick={() => abrirCerrarModalMostrar()}>Cerrar</button>
                    </ModalFooter>
                </Modal>

                <Modal isOpen={modalInsertar}>
                    <ModalHeader>{!editar ? "Insertar" : "Actualizar"} Producto</ModalHeader>
                    <ModalBody>
                        <div className="form-group">
                            <label>Código Producto: </label>
                            <br />
                            <input type="text" className="form-control" autoComplete="off" name="Telefono_Cliente" value={dataClienteForm && dataClienteForm.Telefono_Cliente} onChange={handleFormProductoChange} />
                            <br />
                            <label>Nombre del Producto: </label>
                            <br />
                            <input type="text" className="form-control" autoComplete="off" name="Nombre_Cliente" value={dataClienteForm && dataClienteForm.Nombre_Cliente} onChange={handleFormProductoChange} />
                            <br />
                            <label>Descripción del Producto: </label>
                            <br />
                            <input type="text" className="form-control" autoComplete="off" name="Direccion_Cliente" value={dataClienteForm && dataClienteForm.Direccion_Cliente} onChange={handleFormProductoChange} />
                            <br />
                            <label>Cantidad de Existencia: </label>
                            <br />
                            <input type="number" className="form-control" autoComplete="off" name="Precio" value={dataClienteForm && dataClienteForm.Precio} onChange={handleFormProductoChange} />
                        </div>
                    </ModalBody>
                    <ModalFooter>
                        <button className="btn btn-primary" onClick={() => !editar ? guardarProducto() : actualizarProducto()}>{!editar ? "Guardar" : "Actualizar"}</button>{"   "}
                        <button className="btn btn-danger" onClick={() => abrirCerrarModalInsertar()}>Cancelar</button>
                    </ModalFooter>
                </Modal>
            </Container>
        </div>
    );
}


export default Client;
